import { ReactDOM } from "react";
import React from "react";
import '../App.css';
import Keyboard from "./Keyboard";
import Screen from "./Screen";
function Board(){
  const [equation, setEquation] = React.useState('0');
  
  const handleButtonPressed=(e)=>{
    setEquation(equation+e.target.value);
    console.log(equation)
  };

  let operator=(result,subResult,operation)=>{
    let temp=result;
    if(operation=='+')
    {
      temp+=subResult;

    }
    else if(operation=='-')
    {
      temp-=subResult;
      
    }
    else if(operation=='/')
    {
      temp/=subResult;
      
    }
    else{
      temp*=subResult;
    }
    return temp;
  }
  let getNextNumber=(index,equation)=>{
    let subString="";
    let i=index;
    while(i<equation.length&&equation[i]>='0'&&equation[i]<='9')
    {
      subString+=equation[i];
      i+=1;
    }
    return [i,subString];
  }
  const calcEquation=()=>{
   var result=0;
   var previous='+';
   var subString="";
   var sign=1;
    var i=0;
    while(i<equation.length){
      if(equation[i]=='-')
      {sign=-1;
        i+=1;
      }

      let [index,subString1]=getNextNumber=(i,equation);
      i=index;
      subString=subString1;
      let subResult=parseFloat(subString);
     subResult*=parseFloat(sign);
     result=operator(result,subResult,previous);
    previous=i<equation.length?equation[i]:'';
    sign=1;
    i+=1;
  }
    setEquation(result);
  };
  const resetScreen=()=>
    setEquation('');
 
    return (
        <div className="calculator-board">

          <Screen screenEquation={equation}/>
          <Keyboard onButtonPressed={[handleButtonPressed,calcEquation,resetScreen]} />
          
         
        
        </div>
    )
}

export default Board ;
