import { ReactDOM } from "react";
import '../App.css';
function Screen(props){
     
    return (
      
          <div className="screen">
            <div className="top-layer">
            {props.screenEquation}
            <p className="result">{props.result}</p>
            </div>
          </div>
            
        
        
    )
}

export default Screen ;
