import { ReactDOM } from "react";
import '../App.css';
function Keyboard(){
  const [equation, setEquation] = useState("");
      const buttonPressed=(e)=>{
        setEquation(equation+e.value);
        
      };
    return (
      <div className="keyboard">
           <button  value="1" onClick="{}">1</button>
           <p>{equation}</p>
           <button  onClick="{buttonPressed}">2</button>
           <button  onClick="{buttonPressed}">3</button>
           <button  onClick="{buttonPressed}">+</button>
           <button  onClick="{buttonPressed}">4</button>
           <button  onClick="{buttonPressed}">5</button>
           <button  onClick="{buttonPressed}">6</button>
           <button  onClick="{buttonPressed}">-</button>
           <button  onClick="{buttonPressed}">7</button>
           <button  onClick="{buttonPressed}">8</button>
           <button  onClick="{buttonPressed}">9</button>
           <button  onClick="{buttonPressed}">x</button>
           <span></span>
           <button  onClick="{buttonPressed}">0</button>
           <button  onClick="{buttonPressed}">=</button>
           <button  onClick="{buttonPressed}">/</button>
           
           </div>
    )
}

export default Keyboard ;
